import { useEffect, useState } from "react";

const ListaDocumentos = () => {
  const [documentos, setDocumentos] = useState([]);
  const [editando, setEditando] = useState(null); // Guarda o ID do documento que está sendo editado
  const [tituloEdit, setTituloEdit] = useState("");
  const [conteudoEdit, setConteudoEdit] = useState("");

  useEffect(() => {
    fetch(`${process.env.REACT_APP_BACKEND}/documentos/consulta`)
      .then((res) => res.json())
      .then((data) => setDocumentos(data))
      .catch((error) => console.error("Erro ao buscar documentos:", error));
  }, []);

  const excluirDocumento = (tipo, id) => {
    const url = tipo === "Ata" ? `${process.env.REACT_APP_BACKEND}/atas/${id}` : `${process.env.REACT_APP_BACKEND}/comunicados/${id}`;

    fetch(url, { method: "DELETE" })
      .then((res) => res.json())
      .then(() => setDocumentos(documentos.filter(doc => doc.id !== id)))
      .catch((error) => console.error("Erro ao excluir documento:", error));
  };

  const iniciarEdicao = (doc) => {
    setEditando(doc.id);
    setTituloEdit(doc.titulo);
    setConteudoEdit(doc.conteudo);
  };

  const salvarEdicao = (tipo, id) => {
    const url = tipo === "Ata" ? `${process.env.REACT_APP_BACKEND}/atas/${id}` : `${process.env.REACT_APP_BACKEND}/comunicados/${id}`;

    fetch(url, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ titulo: tituloEdit, conteudo: conteudoEdit }),
    })
      .then((res) => res.json())
      .then((data) => {
        setDocumentos(documentos.map(doc => (doc.id === id ? data : doc)));
        setEditando(null);
      })
      .catch((error) => console.error("Erro ao atualizar documento:", error));
  };

  return (
    <div className="max-w-lg mx-auto mt-10 p-5 border rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">Lista de Atas e Comunicados</h2>
      {documentos.map((doc) => (
        <div key={doc.id} className="p-3 border-b">
          {editando === doc.id ? (
            <div>
              <input
                type="text"
                className="w-full p-2 border rounded mt-1 mb-2"
                value={tituloEdit}
                onChange={(e) => setTituloEdit(e.target.value)}
              />
              <textarea
                className="w-full p-2 border rounded mt-1 mb-2"
                rows="3"
                value={conteudoEdit}
                onChange={(e) => setConteudoEdit(e.target.value)}
              />
              <button
                className="bg-green-500 text-white px-3 py-1 rounded mr-2"
                onClick={() => salvarEdicao(doc.tipo, doc.id)}
              >
                Salvar
              </button>
              <button
                className="bg-gray-500 text-white px-3 py-1 rounded"
                onClick={() => setEditando(null)}
              >
                Cancelar
              </button>
            </div>
          ) : (
            <div>
              <h3 className="font-bold">{doc.titulo}</h3>
              <p className="text-sm">{doc.conteudo}</p>
              <button
                className="bg-blue-500 text-white px-3 py-1 rounded mr-2"
                onClick={() => iniciarEdicao(doc)}
              >
                Editar
              </button>
              <button
                className="bg-red-500 text-white px-3 py-1 rounded"
                onClick={() => excluirDocumento(doc.tipo, doc.id)}
              >
                Excluir
              </button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default ListaDocumentos;
