import { useState } from "react";

const CadastroAtasComunicados = () => {
  const [titulo, setTitulo] = useState("");
  const [conteudo, setConteudo] = useState("");
  const [condominioId, setCondominioId] = useState(""); // Novo campo

  const handleSubmit = (e) => {
    e.preventDefault();

    const novaAta = { 
      titulo, 
      conteudo, 
      condominio_id: 1 // Garante que está enviando um número
    };
    console.log("Chamando API em:", `${process.env.REACT_APP_BACKEND}/atas/`);

    fetch(`${process.env.REACT_APP_BACKEND}/atas/`, { // URL correta!
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(novaAta),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Erro: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Ata cadastrada:", data);
        setTitulo("");
        setConteudo("");
        setCondominioId("");
      })
      .catch((error) => console.error("Erro ao cadastrar ata:", error));
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-5 border rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">Cadastro de Atas e Comunicados</h2>
      <form onSubmit={handleSubmit}>
        <label className="block font-semibold">Título:</label>
        <input
          type="text"
          className="w-full p-2 border rounded mt-1 mb-3"
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
          required
        />

        <label className="block font-semibold">Conteúdo:</label>
        <textarea
          className="w-full p-2 border rounded mt-1 mb-3"
          rows="4"
          value={conteudo}
          onChange={(e) => setConteudo(e.target.value)}
          required
        />

        <label className="block font-semibold">Condomínio ID:</label>
        <input
          type="number"
          className="w-full p-2 border rounded mt-1 mb-3"
          value={condominioId}
          onChange={(e) => setCondominioId(e.target.value)}
          required
        />

        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Cadastrar
        </button>
      </form>
    </div>
  );
};
console.log("Backend URL:", process.env.REACT_APP_BACKEND);

export default CadastroAtasComunicados;
